.board {
	display: flex;
	width: 100%;
	justify-content: space-evenly;
	flex-wrap: wrap;
	padding: 0 20px;
	height: 100%;
	&-card {
		display: flex;
		flex-direction: column;
		width: 100px;
		max-height: 150px;
		background: #410000;
		border: 1px solid;
	}
	&-card.active {
		background: red;
	}
	&-card.player.eliminated {
		.content {
			background: black;
		}
	}
	&-card.player.watching {
		.content {
			background: blue;
		}
	}

	&-card.player {
		background: none;
		border: none;
		text-align: center;
		align-items: center;
		color: #fff;
		.content {
			display: flex;
			@extend %centerFlex;
			background: #410000;
			border-radius: 50%;
			height: 100px;
			width: 100px;
		}
		.name {
			background: #c0c0c094;
			width: 100%;
			display: flex;
			margin-top: 6px;
			padding: 5px 10px;
			border-radius: 99999999px;
			@extend %centerFlex;
		}
	}
}

